import React, { Component } from 'react';

export class SearchEngine extends Component {
    constructor(props) {
        super(props);
        this.state = {
            query: '',
            search: ''
        };
        this.querySearchEngine = this.querySearchEngine.bind(this);
    }

    querySearchEngine(e) {
        let query = e.target.querySelector('input[type="text"]').value;
        console.log(query)
        this.setState({
            query: this.state.query + query
        });
    }
    render() {
        const search = (
            <form>
                <input
                type="text"
                placeholder="What Do You Want to Search?" />
                <button type="submit"> Search </button>
            </form>
        );
        return (
            <div>
              {search}
            </div>
        )
    }
}
