import React, { Component } from 'react';
import logo from './logo.svg';
import instagram from './instagram.png';
import youtube from './youtube.png';
import google from './google.png';
import UI from './UI.png';
import tirto from './tirto.png';
import gmail from './gmail.png';
import './App.css';
import {SearchEngine} from "./SearchEngine";


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      result: ''
    }
    this.getSearch = this.getSearch.bind(this)
  }

  getSearch = async(e) => {
    e.preventDefault();
    const search = e.target.elements.search.value;
    const api_call = await fetch(`https://api.serpwow.com/live/search?api_key=demo&q=${search}`)
    const response = await api_call.json();
    console.log(response)
}

  render() {
    const instagramLink = 'https://www.instagram.com/'
    const googleLink = 'https://www.google.com/'
    const youtubeLink = 'https://www.youtube.com/'
    const sceleLink = 'https://scele.cs.ui.ac.id/'
    const tirtoIdLink = 'https://tirto.id/'
    const mailLink = 'https://mail.google.com/mail/u/0/#inbox'

    return (
        <header className="App-header">
          <div className="App">
            <h1> Welcome Alham Febian Rinaldy!</h1>
            <SearchEngine querySearch={this.getSearch}/>
            <img src={logo} className="App-logo" alt="logo" />
            <h3> What do you like to do? </h3>
            <div className="suggestion">
              <div className="instagramBox">
                <img src={instagram} className="instagram-logo" alt="logo" />
                <a href={instagramLink}> See your friends on Instagram? </a>
              </div>
              <div className="googleBox">
                <img src={google} className="google-logo" alt="logo" />
                <a href={googleLink}> Search something you like? </a>
              </div>
              <img src={youtube} className="youtube-logo" alt="logo" />
              <a href={youtubeLink}> Watch some videos? </a>
            </div>
            <div className="suggestion2">
              <img src={UI} className="youtube-logo" alt="logo" />
              <a href={sceleLink}> Have you seen your academic? </a>
              <img src={tirto} className="youtube-logo" alt="logo" />
              <a href={tirtoIdLink}> Have you seen some news? </a>
              <img src={gmail} className="youtube-logo" alt="logo" />
              <a href={mailLink}> Have you checked your email? </a>

            </div>
          </div>
        </header>
    );
  }
}

export default App;
