import React, { Component } from 'react';

export class SearchEngine extends Component {
    constructor(props) {
        super(props);
        this.state = {
            query: ''
        };
        this.querySearchEngine = this.querySearchEngine.bind(this);
    }

    querySearchEngine(e) {
        let query = e.target.querySelector('input[type="text"]').value;
        this.setState({
            query: this.state.query + query
        });
    }

    go() {
        const link = <a href={this.state.query}> </a>
    }
    render() {
        const search = (
            <form onSubmit={this.go}>
                <input
                type="text"
                placeholder="What Do You Want to Search?" />
                <button type="submit"> Search </button>
            </form>
        );
        return (
            <div>
                <h1>{search}</h1>
            </div>
        )
    }
}
