import React, { Component } from 'react';

export class SearchEngine extends Component {
    constructor(props) {
        super(props);
        this.state = {
            query: 'https://www.google.com/search?q='
        };
        this.querySearchEngine = this.querySearchEngine.bind(this);
    }

    querySearchEngine(e) {
        let query = e.target.querySelector('.search').value;
        this.setState({
            query: <a href= {this.state.query + query}> </a>
        });
    }

    render() {
        return (
            <div>
                <input className="search"
                    type="text"
                    placeholder="What Do You Want to Search" />
                <button onClick={this.querySearchEngine}> Search </button>
            </div>
        )
    }
}
