import React, {Component} from 'react';
import cloud from './cloud.png';
import haze from './haze.png';
import lightrain from './lightrain.png';
import rain from './rain.png';
import './App.css';


export class WeatherApp extends Component{
  constructor(props) {
    super(props);
    this.state = {
      temperature: '',
      city: '',
      humidity: '',
      description: ''
    }
  }

  async componentDidMount() {
    const api_key_weather = '26f7d92635dd0fa76b5fa962eb9d3d96'
    const api_key_time = ''
    const api_call_weather = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=Depok,ID&APPID=${api_key_weather}`)
    const responseWeather = await api_call.json();
    this.setState ({
      temperature: Math.floor(responseWeather.main.temp - 273),
      city: responseWeather.name,
      humidity: responseWeather.main.humidity,
      description: responseWeather.weather[0].description
    });
    console.log(this.state)
  }

  render() {
    if(this.state.description === 'scattered clouds') {
      return (
          <div>
            <h3> Today Weather in {this.state.city} : {this.state.description}</h3>
            <div className="weather">
              <div className="cellWeather cell-1">
                <img src={lightrain} className="cloud-logo" alt="cloud" />
              </div>
              <div className="cellWeather cell-2">
                <h1> Temperature : {this.state.temperature} </h1>
                <br/>
                <br/>
                <h1> Humidity : {this.state.humidity} </h1>
              </div>
            </div>
          </div>
      )
    } else {
      return <h1> yah </h1>
    }
  }
}
