import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';


class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            password: 'alhamfebian',
            authorized: false
        };
        this.authorize = this.authorize.bind(this);
    }

    authorize(e) {
        const password = e.target.querySelector(
            'input[type="password"]').value;
        const auth = password == this.state.password;
        this.setState({
            authorized: auth
        });
    }
  render() {
    const login = (
        <form action="#" onSubmit = {this.authorize}>
            <input
            type="password"
            placeholder="Paswword" />
            <input type="submit" />
        </form>
    );
    const activated = (
        <header className="App-header">
            <img src={logo} className="App-logo" alt="logo" />
        </header>
    );
    return (
      <div className="App">
        <h1> {this.state.authorized ? 'Welcome Alham Febian Rinaldy' : 'Enter the Password'} </h1>
        {this.state.authorized ? activated : login}
      </div>
    );
  }
}

export default App;
