export class Time extends Component {
  constructor(props) {
    super(props);
    this.state = {
      time: new Date().toLocaleString(),
      day: new Date().getDay()
    };
  }

  tick() {
    this.setState({
      time: new Date().toLocaleString(),
      day: new Date().getDay(),
    });
  }

  componentDidMount() {
    this.intervalID = setInterval(
        () => this.tick(),
        1000
    );
  }
  componentWillUnmount() {
    clearInterval(this.intervalID);
  }

  render() {
    return (
        <p className="App-clock">
          The time is {this.state.time}, {this.state.day}
        </p>
    );
  }
}
